let data;function dataLength(){data.results.length;for(const item of data.results){const mainBody=document.querySelector(".main-body"),user=document.createElement("div"),p=document.createElement("p"),image=document.createElement("img");user.classList.add("user"),image.classList.add("image"),p.classList.add("name"),p.innerText=`${item.name.first} ${item.name.last} ${item.phone}`,image.src=item.picture.large,user.appendChild(p),user.appendChild(image),mainBody.appendChild(user)}}fetch("https://randomuser.me/api/?results=1&format=json").then(response=>response.json()).then(dataResponse=>{data=dataResponse,dataLength()}).catch(err=>{});